LPQuery.QueryData:
/*  Exchange 2013 - ActiveSync Proxy: Find OPTIONS requests that contain error responses */
/*  Note: For Exchange 2013 these must be executed against the MBX server logs!          */

SELECT TOP 1000 * FROM '[LOGFILEPATH]'
WHERE ErrorCode IS NOT NULL
OR TO_INT(HttpStatus) > 299
AND Method LIKE '%OPTIONS%'

/* Exchange 2013 ActiveSync Throttling Report - Overbudget counts per user */
SELECT Count(*) As Hits, cs-username as User, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'IsServiceAccount%3a'), 0, '%2c') as [Is Service Account], cs(user-agent) as Device FROM '[LOGFILEPATH]' WHERE cs-uri-query LIKE '%Error:OverBudget%' GROUP BY User, Device, [Is Service Account] ORDER BY Hits, User, Device DESC
/* Duplicate user entries means the user is connecting with more that one device version */

/* Find all SyncKeys per user sorted chronologically */
Select TOP 5000 TO_LOCALTIME(TO_TIMESTAMP(TO_DATE(date), TO_TIME(time))) AS [DateTime], cs-username AS [User], cs(user-agent) as Device, Count(cs-username) AS Total, TO_STRING(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'DeviceId='), 0, '&')) AS DeviceID, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, '_Sk:'), 0, '_') AS SyncKey
FROM '[LOGFILEPATH]' WHERE cs-uri-stem LIKE '%Microsoft-Server-ActiveSync%' AND cs-uri-query LIKE '%_Sk:%' GROUP BY [DateTime], [User], Device, DeviceID, SyncKey ORDER BY [DateTime], [User] ASC

/* Find Device/User Info for 100% CPU while syncing contacts */
Select TOP 5000 TO_TIMESTAMP(TO_DATE(date), TO_TIME(time)) as [Time], cs-username as [User], cs(user-agent) as DeviceID, TO_INT(EXTRACT_PREFIX(EXTRACT_SUFFIX(URLUNESCAPE(cs-uri-query), 0, '_RpcC'), 0, '_')) As RPCCount, sc-status as Status, sc-substatus as SubStatus, sc-bytes as [Bytes], DIV(sc-bytes, 1024) AS [KBytes], time-taken, DIV(time-taken, 1000) as Sec, cs-uri-query FROM '[LOGFILEPATH]' WHERE RPCCount > 2000 /* <-- Change RPC count as needed */ AND cs-uri-query LIKE '%Cmd=Sync%' AND cs-uri-query LIKE '%Ty:Co%' ORDER BY [Bytes] DESC
/* REF: http://support.microsoft.com/kb/2711053 */

/* You will want to enter the http error you want below if you wish to narrow down. IE: 401.1.1326 */
SELECT QUANTIZE(TO_TIMESTAMP(date, time), 3600) AS halfhour, /*3600 = hour, 1800=halfhour, 180= 3min*/ COUNT(*) AS Total, TO_LOWERCASE(cs-uri-stem) AS Vdir, STRCAT(STRCAT(STRCAT(TO_STRING(sc-status),'.'),STRCAT(TO_STRING(sc-substatus),'.')),TO_STRING(sc-win32-status)) as HTTPstatus, cs-uri-stem, cs-uri-query, cs-username, cs(Cookie) INTO '[OUTFILEPATH]\EAShitsperhourwitherrorBadLogon_plus.csv' FROM '[LOGFILEPATH]'
WHERE cs-uri-stem LIKE '%Microsoft-Server-Activesync%' AND HTTPstatus LIKE '%50%' /*change the error here as needed */ GROUP BY halfhour, Vdir, HTTPstatus, cs-uri-stem, cs-uri-query, cs-username, cs(Cookie) ORDER BY halfhour

/* Report counts all errors and warnings per day */
SELECT QUANTIZE(TimeGenerated, 86400) AS Day, COUNT(*) AS [Total Errors] FROM APPLICATION WHERE EventType = 1 OR EventType = 2 GROUP BY Day ORDER BY Day ASC
/* Levels: 1=Error, 2=Warning */

/* Exchange 2013 - EWS CPU usage per 5 minute interval */
SELECT TOP 1000 TO_STRING(QUANTIZE(TO_TIMESTAMP(date, time), 10), 'hh:mm:ss') as Time, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'Request.CPU.Total='), 0, ';') as [CPU Cost] FROM '[LOGFILEPATH]' WHERE cs-uri-query IS NOT NULL AND cs-uri-query LIKE '%Request.CPU.Total%' AND cs-uri-stem LIKE '%/EWS%' ORDER BY [CPU Cost] DESC
/* This is a chart friendly query, just click F6 after the query is complete */

/* Detailed GetUserOOFSettings Report */
SELECT cs-username as User, Count(*) AS Total, REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'SoapAction='), 0, ';'), 'm:', '') As SoapAction, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';MailboxRPCRequests='), 0, ';') As MBXReqs, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';MailboxRPCLatency='), 0, ';') As MBXLat, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';ADRequests='), 0, ';') As ADReqs, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';ADLatency='), 0, ';') As ADLat, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';TimeInGetUserOOFSettings='), 0, ';') As OOFTime, REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, '/Execute:'), 0, ';'), 'msec', '') As Execute, REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ';End('), 0, ')'), 'ms', '') As End, REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, '>Conn:'), 0, ','), 'ms', '') As Conn, REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, ',HangingConn:'), 0, ','), 'ms', '') As HangConn, EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'Resources:'), 0, '(Health') As Target, sc-status as Status, sc-substatus as SubStatus, sc-Win32-Status as WinErr
FROM '[LOGFILEPATH]' WHERE cs-uri-stem LIKE '%ews%' AND cs-uri-query NOT LIKE '%GetEvents%' AND cs-uri-query NOT LIKE '%GetMailTips%' AND cs-uri-query LIKE '%GetUserOofSettingsResponse%' AND cs-username LIKE /* place username between %% -> */ '%%' AND cs-username <> NULL AND SoapAction <> '' GROUP BY cs-username, SoapAction, MBXReqs, MBXLat, ADReqs, ADLat, Execute, End, Conn, HangConn, OOFTime, Target, Status, SubStatus, WinErr ORDER BY Total, OOFTime, User, MBXReqs, MBXLat, ADReqs, ADLat Desc

/* Return All EWS Proxy Requests with 500 Errors */
SELECT cs-username, cs(User-Agent) AS EWSclient, TO_LOWERCASE(cs-uri-stem) AS URI, STRCAT(STRCAT(TO_STRING(sc-status),'.'),TO_STRING(sc-substatus)) as HTTPstatus, sc-substatus As SubStatus, Count(*)
FROM '[LOGFILEPATH]' WHERE HTTPStatus LIKE '%500%' and cs-uri-stem LIKE '%ExchangeWebServicesProxy%' GROUP BY HttpStatus, SubStatus, cs-username, EWSClient, URI ORDER BY cs-username ASC

/* Count EWS SoapActions per user and count.
*/
SELECT REPLACE_STR(EXTRACT_PREFIX(EXTRACT_SUFFIX(cs-uri-query, 0, 'SoapAction='), 0, ';'), 'm:', '') As SoapAction, sc-status AS Status, cs-username as UserName, Count(*) AS Total FROM '[LOGFILEPATH]' WHERE cs-uri-stem LIKE '%ews%' AND cs-username <> NULL AND SoapAction <> '' AND SoapAction <> NULL GROUP BY SoapAction, Status, UserName ORDER BY SoapAction, Total DESC

/* Find all ExRCA test requests; due to limitations this cannot find RPC/HTTP requests or EWS Test */
